import React, { useState } from "react";
import { useHistory } from "react-router-dom";
import { Button } from "react-bootstrap";
import Header from "./Header";
import Footer from "./Footer";
import EmployeeFeed from "./EmployeeFeed";
import EmployerFeed from "./EmployerFeed";
import MatchBlock from "./RecentMatch";

function HomePage() {
  const [employeeFeed, setEmployeeFeed] = useState([
    {
      id: 1,
      name: "Jake",
      age: "33",
      picurl:
        "https://cdn.jsdelivr.net/gh/liangchuxin/perfmatch/images/profile-john.png",
      descrip:
        "Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco.",
    },
    {
      id: 2,
      name: "Milena",
      age: "35",
      picurl:
        "https://cdn.jsdelivr.net/gh/liangchuxin/perfmatch/images/profile-jane.png",
      descrip:
        "Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco.",
    },
    {
      id: 3,
      name: "Joseph",
      age: "53",
      picurl:
        "https://cdn.jsdelivr.net/gh/liangchuxin/perfmatch/images/profile-jack.png",
      descrip:
        "Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco.",
    },
  ]);

  const [employerFeed, setemployerFeed] = useState([
    {
      id: 1,
      name: "John",
      age: "33",
      picurl:
        "https://cdn.jsdelivr.net/gh/liangchuxin/perfmatch/images/profile-john.png",
      descrip:
        "Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco.",
    },
    {
      id: 2,
      name: "Mary",
      age: "35",
      picurl:
        "https://cdn.jsdelivr.net/gh/liangchuxin/perfmatch/images/profile-jane.png",
      descrip:
        "Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco.",
    },
    {
      id: 3,
      name: "Jack",
      age: "53",
      picurl:
        "https://cdn.jsdelivr.net/gh/liangchuxin/perfmatch/images/profile-jack.png",
      descrip:
        "Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco.",
    },
  ]);

  const refreshFeed = () => {
    console.log(employeeFeed);
    const newId = employeeFeed[employeeFeed.length - 1].id + 1;
    const newEmployee = {
      id: newId,
      name: "Unknown" + newId,
      age: Math.floor(Math.random() * 60) + 30,
    };

    setEmployeeFeed([...employeeFeed, newEmployee]);

    console.log(employerFeed);
    const newId2 = employerFeed[employerFeed.length - 1].id + 1;
    const newemployer = {
      id: newId2,
      name: "Unknown" + newId2,
      age: Math.floor(Math.random() * 60) + 30,
    };

    setemployerFeed([...employerFeed, newemployer]);
  };

  const history = useHistory();

  // Tabs
  // function openTab(tabName,elmnt) {
  //   var i, tabcontent, tablinks;
  //   tabcontent = document.getElementsByClassName("tabcontent");
  //   for (i = 0; i < tabcontent.length; i++) {
  //     tabcontent[i].style.display = "none";
  //   }
  //   tablinks = document.getElementsByClassName("tablink");

  //   document.getElementById(pageName).style.display = "block";
  //   elmnt.style.backgroundColor = color;
  // }

  // // Get the element with id="defaultOpen" and click on it
  // document.getElementById("defaultOpen").click();

  return (
    <div className="App">
      <Header />
      {/* <EmployeeFeed employeeFeed={employeeFeed} /> */}
      {/* Employer 还是 Employee? */}
      <div className="Find">
        {/* <button onClick={() => history.push("/joinEmployer")}>join</button> */}
        <div className="findEmployer">
          <h2>I'm an employee</h2>
          <p className="description">
            Seeking for a suitable family to work for? We strive to help you
            find you the most suitable employer.
          </p>
          <a className="findButton">FIND EMPLOYER NOW</a>
        </div>
        <div className="findEmployee">
          <h2>I'm an employer</h2>
          <p className="description">
            Thinking of hiring a FDW? We strive to help you find you the most
            suitable helper for your home.
          </p>
          <a className="findButton">FIND EMPLOYEE NOW</a>
        </div>
      </div>
      {/* Employer Helper Feed */}
      <div className="Feed">
        <h2 className="bar">Recent matches</h2>
        <div className="feedTab">
          <a className="active">
            <h2>Employers</h2>
          </a>
          <a>
            <h2>Helpers</h2>
          </a>
        </div>
        <MatchBlock className="employerList" employeeFeed={employerFeed} />
        <MatchBlock className="employeeList" employeeFeed={employeeFeed} />
        <a className="logReg">Get personalised list</a>
      </div>
      {/* 咱能干啥？ */}
      <div className="Features">
        <div className="description">
          <p className="capital">HOW TO GET STARTED WITH PERF MATCH?</p>
          <h2 className="slogan">
            Find your <span className="underline">perfect</span> match within
            <br />a few minutes.
          </h2>
          <h2 className="slogan accent">No payment required.</h2>
          <p className="intro">
            Lorem ipsum dolor sit amet, consectetur adipiscing elit,{" "}
            <span className="light">
              sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.
              Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris
              nisi ut aliquip ex ea commodo consequat.
            </span>
          </p>
        </div>
        <div className="featuresContainer">
          <div className="featuresCard">
            <div className="cardContainer">
              <p className="step">1. Create an account</p>
              <p className="instruction">Go to registration page to sign up</p>
            </div>
          </div>
          <div className="featuresCard">
            <div className="cardContainer">
              <p className="step">2. Fill in information</p>
              <p className="instruction">Type personal information in page</p>
            </div>
          </div>
          <div className="featuresCard">
            <div className="cardContainer">
              <p className="step">3. Submit your responses</p>
              <p className="instruction">Do not reload before submitting</p>
            </div>
          </div>
          <div className="featuresCard">
            <div className="cardContainer">
              <p className="step">4. Wait for email</p>
              <p className="instruction">We will notify you when finished</p>
            </div>
          </div>
          <div className="featuresCard">
            <div className="cardContainer">
              <p className="step">5. Go through the choices</p>
              <p className="instruction">Lists of employers and employees</p>
            </div>
          </div>
          <div className="featuresCard">
            <div className="cardContainer">
