import React, { useState, useEffect } from "react";
import { useHistory } from "react-router-dom";
import { Button } from "react-bootstrap";
import Header from "./Header";
import Footer from "./Footer";
import "../style/SignUp.scss";
// icon
import arrowRight from "../icons/arrow-right.svg";
import lock from "../icons/lock.svg";
import user from "../icons/user.svg";
import mail from "../icons/mail-light.svg";

function Login() {
  useEffect(() => {
    window.scrollTo(0, 0);
  }, []);
  return (
    <div className="App">
      <Header />
      <div className="container">
        <h1>Login</h1>
        <form>
          <span className="icoContainer">
            <span className="icoPlaceholder">
              <img src={user} />
            </span>
            <input placeholder="Username *" />
          </span>
          <span className="icoContainer">
            <span className="icoPlaceholder">
              <img src={lock} />
            </span>
            <input type="password" placeholder="Password *" />
          </span>
          <span>

            <label className="newLabel" for="remember"><input type="checkbox" id="remember" value="REMEMBER ME" /><span>REMEMBER ME</span></label>
          </span>
          <button>
            LOGIN
            <span style={{ marginLeft: "20px" }} className="arrow">
              <img src={arrowRight} />
            </span>
          </button>
        </form>
      </div>
      <Footer />
    </div>
  );
}
export default Login;
